package lucasfscr.com.caraoucoroa;import android.content.Intent;import android.support.annotation.Nullable;import android.support.v4.content.ContextCompat;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.view.View;import android.widget.ImageView;import butterknife.BindView;import butterknife.ButterKnife;public class DetalheActivity extends AppCompatActivity {    @Nullable    @BindView(R.id.imgMoedaID)    ImageView imgMoeda;    @Nullable    @BindView(R.id.imgVoltarID)    ImageView imgBtnVoltar;    private Bundle extra;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_detalhe);        ButterKnife.bind(this);        extra = getIntent().getExtras();        if(extra != null){            String opcao = extra.getString("opcao");            if(opcao.equals("cara")){                imgMoeda.setImageDrawable(ContextCompat.getDrawable(this, R.drawable.moeda_cara));            }else{                imgMoeda.setImageDrawable(ContextCompat.getDrawable(this, R.drawable.moeda_coroa));            }        }    }    @Override    protected void onStart() {        super.onStart();        imgBtnVoltar.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                startActivity(new Intent(DetalheActivity.this, MainActivity.class));            }        });    }}