package lucasfscr.com.caraoucoroa;import android.content.Intent;import android.support.annotation.Nullable;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.view.View;import android.widget.ImageView;import java.util.ArrayList;import java.util.Random;import butterknife.BindView;import butterknife.ButterKnife;public class MainActivity extends AppCompatActivity {    @Nullable    @BindView(R.id.imgJogarId)    ImageView imageJogar;    private String[] opcao = {"cara", "coroa"};    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        ButterKnife.bind(this);    }    @Override    protected void onStart() {        super.onStart();        imageJogar.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Random rand = new Random();                int randNumber = rand.nextInt(2);                Intent intent = new Intent(MainActivity.this, DetalheActivity.class);                intent.putExtra("opcao", opcao[randNumber]);                startActivity(intent);            }        });    }}